import {useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";


const url = `https://jsonplaceholder.typicode.com/users`

export default () => {

     var [userData, setStateUserData] = useState({
              uname: '',
              email: '',
              phone: '',
              textConformation: ''
        }) 


          //Destructuring...
