   let formDatahandling=(e)=>
   {
         setStateUserData(...userData, {[e.target.name]: e.target.value });
   }

    let submitHandler=(e)=>
    {
        e.preventDefault();
        var FormData=
        {
            'name': userData.name,
            'email': userData.email,
            'phone': userData.phone
        } 
    
   
        axios.post(url, FormData).then( ()=>{ 
            //window.alert("....CREATED....")
            useHistory('/restapi')
        });
    

    
        return (<>
           <div className="container p-3">

             <h4 className="py-3 text-center">USER REGISTRATION</h4>

             <div className="container w-75">

             <form onSubmit={this.submitHandler.bind(this)}>
             <div class="mb-3">
             <label class="form-label">Name</label>
             <input type="text" class="form-control"
                value={name} name="name"
                onChange={formDatahandling.bind(this)} />
             <p>{uname}</p>
            </div>

             <div class="mb-3">
             <label class="form-label">Email</label>
             <input type="email" class="form-control" value={email} name="email" onChange={formDatahandling.bind(this)} />
             <p>{email}</p>
            </div>

            <div class="mb-3">
             <label class="form-label">Phone</label>
             <input type="number" class="form-control" value={phone} name="phone" onChange={form.bind(this)} />
             <p>{phone}</p>
            </div> 

            <div class="mb-3">
             
             <button type="submit" class="btn btn-primary">CREATE ACCOUNT</button>
            </div>

            <p class="text-primary">{textConformation}</p>
            
             </form>

             </div>
           </div>
        
        </>)
    }

