import  { React, useState, useEffect } from "react";
import axios from 'axios';
const apiurl =`https://jsonplaceholder.typicode.com/posts`


export default (props) => {

    //State Object Implementation by HOOKS
    var [posts, setStatePosts] = useState([]);

    // API CALLS by HOOKS
    useEffect(()=>
    {
        axios.get(apiurl).then((result)=> setStatePosts(result.data))
    
    },[])


    return (<>
                 <div class="container">

                 <h1 class="text-center">React UseEffect Hooks for API CALLS</h1>

                <div className="row">

                {
                    posts.map((res, i) => {
                          return( <ul key={ i } >

                             <li>
                                <h4>{res.id}.{res.title}</h4>
                                <p>{res.body}</p>
                             </li>

                          </ul>) 
                    })
                 }

                </div>
                 
                 </div>

                 
    </>)
}
