import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


let url = `https://jsonplaceholder.typicode.com/users`


export default (props) => {

       
   let [userData, setStateUserData] = useState = ({
        name:'',
        email:'',
        phone:''

    });
        //get_parameter_value with destructuring
        let { idno } = useParams();

        

        useEffect(() =>{
             axios 
                  .get(`${url}/${idno}`)
                  .then((result) => setStateUserData(result.data))
        },[]);

        //Destructuring...
        var { e, email, phone } = userData
          

        let formDatahandling  = (e) => {   
        setStateUserData({...userData,[e.target.name]:e.target.value});
    }

  
}

  
 
   let submitHandler = (e) => {
     e.preventDefault();
 
     var FormData=
     {
         name: `${userData.name}`,
         email: `${userData.email}`,
         phone: `${userData.phone}`
     }
 
      axios.put(`${url}/${idno}`, FormData)
      .then(() => {
      props.history.push('/restapi');
     
   });

    return (<>
      
        <div className="container p-5">
          <h2>EDIT COMPONENT PAGE</h2>

          <form onSubmit={submitHandler.bind(this)}>

            <div className="mb-3">
              <label  className="form-label">Name </label>
              <input type="text" className="form-control" value={name} name="name" onChange={formDatahandling.bind(this) }/>               
            </div>

            <div className="mb-3">
              <label  className="form-label">Email </label>
              <input type="email" className="form-control" value={email} name="email" onChange={formDatahandling.bind(this) } />               
            </div>

            <div className="mb-3">
              <label  className="form-label">Phone No </label>
              <input type="number" className="form-control" value={phone} name="phone" onChange={formDatahandling.bind(this) }/>               
            </div>

            <div className="mb-3">
            <Link className="btn btn-success mx-2" to="/hooksuser"> GOTO BACK </Link>
              <button type="submit" className="btn btn-warning text-white" value="update">Update</button>             
            </div>

          </form>   

          
        </div>
      
   </>);
   
  }
