import {React, Component} from 'react';

//step-1
import { connect } from 'react-redux';



//step-3
import EmpAction from '../../Actions/empAction';  //ExternalData-RESTAPI

import { bindActionCreators } from 'redux';


class ReactReduxConnection extends Component 
{
       constructor(props)
       {
        super(props);
        this.state = {}
       }

       componentDidMount()
       {
           this.props.EmpAction();
       }

       render() {
            return (<> 
            
                <div className='container p-2 '>

                <h2 className='text-center'> ReactReduxConnection Example </h2>

                </div>

            </>)
       }
}
//step-6
mapStateToProps = (state) => {

//get The data from the Redux_store by using Reducers[state objects]

console.log(state)

 return({
    empdata: state.empReducer
 })
}
//step-4
mapDispatchToProps = (dispatch) => {
// calling Redux actions 
return bindActionCreators({ EmpAction }, dispatch) 
}



//export default ReactReduxConnection-> Normal React Component  Export

//step-2
export default connect(mapStateToProps,mapDispatchToProps)(ReactComponent)
