import React, { useState, useEffect } from "react";
import axios from "axios"
import { Link } from "react-router-dom";
import { getAccordionDetailsUtilityClass } from "@mui/material";


//axios.get(url)

//Global_variable for RESTAPI_URL
const url = `https://jsonplaceholder.typicode.com/users`//array type of Object

export default () =>  
 {
    let [userData, setStateuseData] = useState=([]); 
    

   function getData()
    {
        axios.get(url).then((result) => setStateuseData(result.data))
    }

    useEffect(() => { getData() }, []);


//Delete the data
let DeleteHandler = (id) =>
{
     if (window.confirm(`Delete the Recorded Number Is ${id} ? `)) 
     {
        axios.delete(`${url}/${id}`)
        getData()
     }
}



    return (<>
        <div className="container p-5">


            <h3 className="text-center">FETCH RESTAPI DATA USING LOCAL URL/CLIENT</h3>

            <table className="table table-dark table-bordered">

             <thead className="table table-light  my-5">
                <tr>
                    <th>ID</th>
                    <th>NAME</th>
                    <th>EMAIL</th>
                    <th>PHONE</th>
                    <th colSpan="3" className="text-center">ACTION</th>
                </tr>
             </thead>
           
            <tbody>

            {
                 userData( (res,i)=>{
                    return(<tr key={i}>
                        <td>{res.id}</td>
                        <td>{res.name}</td>
                        <td>{res.email}</td>
                        <td>{res.phone}</td>
                        <td>
                            <Link to={`/restapi/view/${res.id}`} className="btn btn-primary">View</Link>
                        </td>
                        <td>
                            <Link to={`/restapi/edit/${res.id}`} className="btn btn-warning">Edit</Link>
                        </td>
                        <td>
                            <button onClick={DeleteHandler(res.id)} className="btn btn-danger">Delete</button>
                        </td>
                    </tr>) 
                })
            }
            </tbody>
             </table>

        </div>
    </>)


        }
