import React,{ Component } from "react";
import axios from 'axios';
import { Link } from "react-router-dom";

const url=" http://localhost:3003/users"
  class Restapi extends Component
{
    constructor(props) 
    {

        super(props)
        this.state ={
             userData:[]
        }
    }
    
    /*DeleteHandler=(id)=>
    {
      
        
       if( window.alert(`Selected Recors number is ${id} ?`)){
        axios.delete(`${url}/${id}`)
