    render(){
         console.log(this.props)
         console.log(url)
        return(<> 
        
            <div className="p-5 text-center">
           <h2 className="text-center">FETCH RESTAPI USING GLOBAL URL/CLIENT</h2>
           <table className="table table-dark table-bordered">
             <thead>
                 <tr>
                     <th>id</th>
                     <th>name</th>
                     <th>email</th>
                     <th>website</th>
                     <th colSpan="4" className="text-center">Action</th>
                 </tr>
             </thead>
             <tbody>

             
   {
         
         this.state.userData.map((res,i)=>{
             return(<tr key={i}>
                  <td>{res.id}</td>
                  <td>{res.name}</td>
                  <td>{res.email}</td>
                  <td>{res.website}</td>
                  
                  <td><Link to={`/restapi/view/${res.id}`} className="btn btn-primary">view</Link></td>

                  <td><Link to={`/restapi/view/${res.id}`}className="btn btn-danger">edit</Link></td>
                  <td><button  onClick={()=>this.DeleteHandler(res.id)} className="btn btn-danger">Delete</button></td>
                  <td><button className="btn btn-secondary">view</button></td>
          </tr>   )
         })

          }
          
          </tbody>
            </table>
            </div>
            </>  )
    }
    componentDidMount(){  axios.get(url).then((result)=> this.setState({userData:result.data}));}
}
export default Restapi
